    //随机生成一个可用的ip
    public static HttpHost generatorProxyHost(List<String> list) throws Exception{
        int size= list.size();
        //产生随机数
        int num =(int)(Math.random() * size);
        HttpHost proxy = new HttpHost(list.get(num).split("\t")[0],Integer.parseInt(list.get(num).split("\t")[1]));
        return proxy;
    }

